mapper.select:
select articleno, subject, userid, content, regtime, answer
  from qna 
 where articleno = #{articleno}

mapper.insert:
insert into qna(
	subject, userid, content
) values (
	#{subject}, #{userid}, #{content}
)

mapper.update:
update qna 
   set subject = #{subject}, 
   	   userid = #{userid}, 
   	   content = #{content},
   	   answer = #{answer}
 where articleno = #{articleno}
